INSERT INTO eshop.products (name, slug, summary, description, created, updated, stock, category_id, showed, deleted, galleon_price, sickle_price, knut_price) VALUES ('Cleansweep 2', 'cleansweep-2', 'Létající koště Cleansweep Two - inovativní design a technologické prvky spojené v jednom pohodlném a stylovém doplňku pro každodenní dobrodružství.', '<p>Osvoboďte se od země s létajícím koštětem Cleansweep Two, které spojuje bezpečnost, pohodlí a elegance ve svěžím designu. Toto kouzelnické dopravní prostředek není pouze prostředkem k cestování, ale symbolem svobody a inovace.</p>
