INSERT INTO eshop.products (name, slug, summary, description, created, updated, stock, category_id, showed, deleted, galleon_price, sickle_price, knut_price) VALUES ('Průvodce přeměňováním pro začátečníky', 'pruvodce-premenovanim-pro-zacatecniky', 'Vstupte do fascinujícího světa magie s knihou "Průvodce přeměňováním pro začátečníky". Skvělý průvodce pro ty, kteří touží po objevování kouzelnických dovedností a tajemství.', '<p>Tato kniha otevírá dveře kouzelnického umění před těmi, kteří se chtějí ponořit do světa přeměňování. "Průvodce přeměňováním pro začátečníky" je komplexním průvodcem pro ty, kteří hledají hlubší porozumění a ovládnutí magických dovedností.</p>
